-- How to fetch how much data we have processed in daily, monthly and yearly basis
SELECT * FROM sys.dm_external_data_processed;

-- How to see the data processed limits
SELECT * FROM sys.configurations
WHERE name LIKE 'Data Processed %';

-- How to set data processed limits
sp_set_data_processed_limit
    @type = N'monthly', -- Like that we can write Daily, Weekly to set limits
    @limit_tb = 4;
